mapper.select:
select vip_ID,vip_Gender,vip_Name,vip_Birthday,vip_Telephone,vip_Age
,vip_Address,vip_Time
from   vip_Information


vip_ID=#{vip_ID}


and vip_Name=#{vip_Name}

mapper.update:
update vip_Information
vip_Name=#{vip_Name}, vip_Birthday=#{vip_Birthday}, vip_Telephone=#{vip_Telephone}, vip_Address=#{vip_Address}, vip_Age=#{vip_Age}
where vip_ID=#{vip_ID}
